    public int findMaxDiff(int[] arr) {
        int maxDiff = arr[1] - arr[0];
        int min = 0;

        for (int i = 1; i < arr.length; ++i) {
            int nextMaxDiff = arr[i] - min;
            maxDiff = Math.max(maxDiff, nextMaxDiff);
            min = Math.min(min, arr[i]);
        }
