#!/bin/python3

def pokemon():
    # complete the function
    pkmn = list(map(int, input().rstrip().split()))
    pokemons = {}
    for i in pkmn:
        species = i % 100
        level = i // 100
        pkm = [species, level]
        pokemons.setdefault(species, [])
        pkm.append(pokemons.get(species))
        pokemons[species] = pkm
    for key, value in sorted(pokemons.items()):
        return str(' '.join(map(str, value)))

print(pokemon())

