# Definition for singly-linked list.
class ListNode:
    def __init__(self, val=0, next=None):
        self.val = val
        self.next = next
class Solution:
    def mergeKLists(self, lists: List[ListNode]) -> ListNode:
        head = ListNode()
        tail = head
        q = lists[:]
        while q:
            min_q=[]
            while q:
                front=q.pop(0)
                if not min_q or front.val < min_q[0].val:
                    min_q=front,
                q=[ele for ele in q if ele.val>min_q[0].val]
            tail.next= min_q[0]
            tail=min_q[0]
        return head.next

